import React, { Component } from 'react';
import { ReactComponent as Logo } from './images/logo.svg';
import { ReactComponent as Lines } from './images/three-horizontal-lines.svg';

const videoResolution = { width: 300, height: 300 };

type Props = {
	appState: any,
	setView: any,
}

type State = {
	photoStatus: string,
}

enum Views {
	account = 'ACCOUNT'
	, register = 'REGISTER'
	, photo = 'PHOTO'
}

enum PhotoStatus {
  capture = 'capture'
  , finish = 'finish'
}

export default class Photo extends Component<Props, State> {
	private videoRef = React.createRef<HTMLVideoElement>();
	private videoCanvasRef = React.createRef<HTMLCanvasElement>();
	private video: any;
	// private canvas = document.createElement('canvas');
	private streams: any[] = [];

	constructor(props: Props) {
		super(props);
		this.state = {
			photoStatus: PhotoStatus.capture,
		};
	}

	componentDidMount() {
		this.video = this.videoRef.current as any;

		this.showVideo();
	}

	showVideo() {
		const video: any = {
			width: videoResolution.width,
			height: videoResolution.height,
		};
		window.navigator.mediaDevices.getUserMedia({
			video,
			audio: false,
		}).then((stream) => {
			if (stream === null) return;
			this.video.srcObject = stream;
			this.streams.push(stream);
			try {
				this.video.play();
			} catch (err) {
				console.log(err);
				return;
			}
		}).catch((err) => {
			console.log(err);
		});
	}

	takePhoto = () => {
		const videoWidth = videoResolution.width;
		const videoHeight = videoResolution.height;
		const canvas = document.getElementById("canvas") as HTMLCanvasElement;
    const ctx = canvas.getContext('2d');
		canvas.width = videoHeight;
		canvas.height = videoWidth;
		console.log(ctx)
		if (ctx) ctx.drawImage(this.video, 0, 0, videoHeight, videoWidth, 0, 0, videoHeight, videoWidth);
		this.setState({ photoStatus: PhotoStatus.finish });
		this.stopVideo();

		const data = canvas.toDataURL('image/jpeg');
    return data;
	}

	stopVideo = (): void => {
    try {
      this.streams.forEach((stream: any) => {
        stream.getTracks().forEach((a: any) => {
          a.stop();
        });
      });
      this.streams = [];
    } catch (err) {
      console.log('stopVideo error', err);
    }
  }

	render() {
		const { setView } = this.props;
		const { photoStatus } = this.state;
		let photoButtons: any;

		switch (photoStatus) {
			case PhotoStatus.capture:
				photoButtons = (
					<button
						className="button"
						type="button"
						onKeyUp={() => this.takePhoto()}
						onClick={() => this.takePhoto()}
					></button>
				);
				break;
			case PhotoStatus.finish:
				photoButtons = (
					<div>
						<div
							className="lines"
							role="button"
							onKeyUp={() => setView(Views.register)}
							onClick={() => setView(Views.register)}
						>No</div>
						<div
							className="lines"
							role="button"
							onKeyUp={() => setView(Views.register)}
							onClick={() => setView(Views.register)}
						>Yes</div>
					</div>
				);
				break;
			default:
				break;
		}

		return (
			<div className='photo-component'>
				<div className="header">
					<Logo />
					<div
						className="lines"
						role="button"
						onKeyUp={() => setView(Views.register)}
						onClick={() => setView(Views.register)}
					><Lines /></div>
				</div>
				<div className="main">
					<canvas id="canvas" ref={this.videoCanvasRef} />
					<video className="" ref={this.videoRef}></video>
					<div className="buttons-section">
						{photoButtons}
					</div>
				</div>
			</div>
		);
	}
}
