import React, { Component } from 'react';
import { ReactComponent as Logo } from './images/logo.svg';
import { ReactComponent as Lines } from './images/three-horizontal-lines.svg';
import { ReactComponent as Photo } from './images/photo.svg';

const videoResolution = { width: 300, height: 300 };

type Props = {
  appState: any,
  setView: any,
}

enum Views {
  account = 'ACCOUNT'
  , register = 'REGISTER'
}

export default class Account extends Component<Props> {
  private videoRef = React.createRef<HTMLVideoElement>();
  private video: any;

  componentDidMount() {
    this.video = this.videoRef.current as any;
  }

  takePhoto() {
    const video: any = {
      width: videoResolution.width,
      height: videoResolution.height,
    };
    window.navigator.mediaDevices.getUserMedia({
      video,
      audio: false,
    }).then((stream) => {
      if (stream === null) return;
      this.video.srcObject = stream;
      try {
        this.video.play();
      } catch (err) {
        console.log(err);
        return;
      }
    }).catch((err) => {
      console.log(err);
    });
  }

  render() {
    const { setView } = this.props;

    return (
      <div className='account-component'>
        <div className="header">
          <Logo />

          <div
            className="lines"
            role="button"
            onKeyUp={() => setView(Views.register)}
            onClick={() => setView(Views.register)}
          ><Lines /></div>
        </div>
        <div className="main">
          <div className="new-photo-section">
            <Photo />
            <div>Would you like to share your moment?</div>
          </div>
          <div></div>
        </div>
        {/* <video ref={this.videoRef}></video>
        <button
          type="button"
          onKeyUp={() => this.takePhoto()}
          onClick={() => this.takePhoto()}
        >Take a photo</button> */}
      </div>
    );
  }
}
